import React, { useState } from 'react';
import './contact.css';

const Contact = ({ nom }) => {
  const [status, setStatus] = useState('connected'); // 'connected' ou 'disconnected'

  // Fonction pour changer le statut - à utiliser selon la logique de votre application
  const toggleStatus = () => {
    setStatus(status === 'connected' ? 'disconnected' : 'connected');
  };

  // Couleur de fond basée sur le statut
  const backgroundColor = status === 'connected' ? 'green' : 'red';

  return (
    <div className="profil">
      <img src='https://pbs.twimg.com/profile_images/1267052300964638720/JdScoFy__400x400.jpg' className="profil-image" />
      <div>
        <button className='connection-statut' onClick={toggleStatus}>{statut}</button>
      </div>
      <h2 className="nom">John Doe</h2>
    </div>
  )
};

export default Contact;
