import React, { useState } from 'react';
import './App.css';
import Contact from './components/Contact.jsx';


const App = () => {
  // État pour gérer le nom et l'image de l'utilisateur
  const [user, setUser] = useState({
    nom: 'Nom Initial', // Remplacez par le nom initial de l'utilisateur
    image: 'lien_vers_image_initial' // Remplacez par le lien initial de l'image de l'utilisateur
  });

  // Fonction pour changer le nom et l'image de l'utilisateur
  const changeUserInfo = (newName, newImage) => {
    setUser({
      nom: newName,
      image: newImage
    });
  };

  return (
    <div>
      <Contact
        nom={user.nom}
        image={user.image}
        changeUserInfo={changeUserInfo}
      />
    </div>
  );
};

export default App;
