import React, { useState } from 'react';
import './contact.css';

const Contact = ({ nom, image, changeUserInfo }) => {
  const handleUserChange = () => {
    changeUserInfo('Nouveau Nom', 'lien_vers_nouvelle_image');
  };

  const [status, setStatus] = useState('connected'); // 'connected' ou 'disconnected'

  // Cette fonction pourrait être appelée pour changer le statut
  const toggleStatus = () => {
    setStatus(status === 'connected' ? 'disconnected' : 'connected');
  };

  return (
    <div className="profil">
      <img 
        className="profil-image"
        src={image} 
        alt={`avatar de ${nom}`} />
      <div>
        <span className={`connection-statut ${status}`}></span>
      </div>
      <h2 className="nom">{nom}</h2>
    </div>
  )
};

export default Contact;
