  const [user, setUser] = useState({
    nom: 'Madame Quelconque', // Remplacez par le nom initial de l'utilisateur
    image: 'https://pbs.twimg.com/profile_images/1267052300964638720/JdScoFy__400x400.jpg' // Remplacez par le lien initial de l'image de l'utilisateur
  });

   // Fonction pour mettre à jour les informations de l'utilisateur
   const updateUser = (event) => {
    event.preventDefault();
